import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import './index.css'

export const Movie = () => {
  const [title, setTitle] = useState('')
  const [data, setData] = useState([])
  const [movieDetails, setMovieDetails] = useState(null)

  const handleChange = (event) => {
    setTitle(event.target.value)
  }

  const handleSubmit = async () => {
    setTitle(title)
    let response = await axios.get(`https://www.omdbapi.com/?s=${title}&apikey=72a91ec7`)
    console.log(response.data.Search)
    setData(response.data.Search)
  }

  useEffect(() => {
    if (data.length > 0) {
      const fetchMovieDetails = async () => {
        const response = await axios.get(`https://www.omdbapi.com/?i=${data[0].imdbID}&apikey=72a91ec7`)
        setMovieDetails(response.data)
      }
      fetchMovieDetails()
    }
  }, [data])

  return (
    <div>
      <input type="text" name="" onChange={handleChange} placeholder="Enter the Title " id="search" />
      <button onClick={handleSubmit} id="btn">Search</button>
      {movieDetails ? (
        <div className="flex-container">
          {data.map((item) => (
            <>
              <div className="poster">
                <Link to={`/details/${item.imdbID}`}>
                  <img src={item.Poster} alt="" id="post" />
                </Link>
              </div>
            </>
          ))}
        </div>
      ) : (
        <div className="error">No Movie Found</div>
      )}
    </div>
  )
}
